import requests
import json
import matplotlib.pyplot as plt
import pandas as pd
import numpy as np

# This Code makes a report with the Info of several series provided by the INEGI API
# containing the start, the end date of the series and the number of values

def GetFromINEGI( Serie, API ,historic='false'):
    #Get's the data from the INEGI API and returns a DataFrame with the Data and the Dates
    global content

    url='https://www.inegi.org.mx/app/api/indicadores/desarrolladores/jsonxml/INDICATOR/'+Serie+'/es/0700/'+historic+'/BISE/2.0/'+API+'?type=json'

    DataFrame = pd.DataFrame(data={})

    'Request the info from INEGI API'
    response = requests.get(url)

    'If the response is 200, everything is OK'
    if response.status_code == 200:

        'content is all the data that gets from the API'
        content = json.loads(response.content)

        'Observations is the list that will have the historic data that we want'
        Observations = []

        'Dates is the list that will have the Dates of the taken data'
        Dates = []
        'Check if the series is empty'
        if content['Series'] is not None:

            for obs in range(len(content['Series'][0]['OBSERVATIONS'])):

                'Access to the JSON file to extract the series'
                Series=content['Series'][0]['OBSERVATIONS'][obs]['OBS_VALUE']
                Date=content['Series'][0]['OBSERVATIONS'][obs]['TIME_PERIOD']

                try:
                    'Transform the data to float and add it to the list Observations'
                    Observations.append(round(float(Series),3))
                    Dates.append(Date)
                except:
                    print('There is a problem with the observation ',obs, 'in the serie', Serie )
                    pass


            'Return the list with all the series'
            DataFrame['Data']=Observations
            DataFrame['Date']=Dates
    else:
        print('Error with the response')
    return DataFrame

